import React, { useState } from 'react';
import axios from 'axios';
import Search from './comp/SearchBox';
import Results from './comp/MovieList';
import Detail from './comp/MovieDesc';
import './App.css';

function App() {
const [state, setState] = useState({
	s: "sherlock",
	results: [],
	selected: {},
});

const apiurl = "https://www.omdbapi.com/?apikey=a2526df0";

const searchInput = (e) => {
	let s = e.target.value;

	setState((prevState) => {
	return { ...prevState, s: s };
	});
};

const search = (e) => {
	if (e.key === "Enter") {
	axios(apiurl + "&s=" + state.s).then(({ data }) => {
		let results = data.Search;

		console.log(results);

		setState((prevState) => {
		return { ...prevState, results: results };
		});
	});
	}
};

const openDetail = (id) => {
	axios(apiurl + "&i=" + id).then(({ data }) => {
	let result = data;

	setState((prevState) => {
		return { ...prevState, selected: result };
	});
	});
};

const closeDetail = () => {
	setState((prevState) => {
	return { ...prevState, selected: {} };
	});
};

return (
<>
	<div className="navbar">
		<div className="nav">
			<h4>IMDb</h4>
		</div>
		<div className="search">
			<Search searchInput={searchInput} search={search} />
		</div>
	</div>
	<div className="box">
		<div className="b2">
			<h2>Featured Today</h2>
		</div>
	</div>
	<main>
	  <div className='container-fluid movie-app'>
		  <div className='row'>
			  <Results results={state.results} openDetail={openDetail} />

			  {typeof state.selected.Title != "undefined" ? (
			  <Detail selected={state.selected} closeDetail={closeDetail} />
			  ) : (
			  false
			  )}
		  </div>
	  </div>
	</main>
</>
);
};

export default App;
