import React from "react";

function Search({ searchInput, search }) {
return (
	<div className="col col-sm-4">
	<input
		type="text"
		placeholder="Search for a Movie..."
		className="search"
		onChange={searchInput}
		onKeyPress={search}
	/>
	</div>
);
}

export default Search;


