import React,{useState,useEffect} from "react";
import SearchBox from './SearchBox';
import "./Header.css"
import { Link } from "react-router-dom"

const Header = () => {
    const [movies, setMovies] = useState([]);
    const [searchValue, setSearchValue] = useState('');

    const getMovieRequest = async (searchValue) => {
      const url = `https://api.themoviedb.org/3/movie/${searchValue}?api_key=4e44d9029b1270a757cddc766a1bcb63&language=en-US`;

      const response = await fetch(url);
      const responseJson = await response.json();

      if (responseJson.Search) {
        setMovies(responseJson.Search);
      }
    };

    useEffect(() => {
      getMovieRequest(searchValue);
    }, [searchValue]);

    return (
      <>
        <div className="header">
            <div className="headerLeft">
                <Link to="/"><img className="header__icon" src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/69/IMDB_Logo_2016.svg/2560px-IMDB_Logo_2016.svg.png" /></Link>
                <SearchBox searchValue={searchValue} setSearchValue={setSearchValue} />
            </div>
        </div>
        <div className="nav">
          <h1><b>Featured today</b></h1>
        </div>
      </>
    )
}

export default Header
