        </*/AppName*//div>
        </*SearchBox*/div className="flex flex-row px-2.5 py-2.5 rounded-md ml-5 w-2/4 bg-white">
          </*SearchInput*/input className="text-black text-base font-bold border-none outline-none ml-3.5"
            placeholder="Search Movie"
            value={searchQuery}
            onChange={onTextChange}
          />
        </*/SearchBox*//div>
      </*/Header*//div>
      {selectedMovie && <MovieInfoComponent selectedMovie={selectedMovie} onMovieSelect={onMovieSelect}/>}
      </*MovieListContainer*/div className="flex-auto flex-row flex-wrap p-8 gap-6 justify-evenly">
        {movieList?.length ? (
          movieList.map((movie, index) => (
            <MovieComponent
              key={index}
              movie={movie}
              onMovieSelect={onMovieSelect}
            />
          ))
        ) : (
          <>
          </*Heading*/div className="bg-black p-5">
            </*Yellow*/h1 className="text-yellow-500 text-4xl font-bold">Featured Today</*Yellow*//h1>
          </*Heading*//div>
          <Home/>
          </>
        )}
      </*/MovieListContainer*//div>
    </*Container*//div>
  );
}

export default App;
