import React, { useEffect, useState } from "react";
import Axios from "axios";
import { API_KEY } from "../App";
import styled from "styled-components";

/*const Container = styled.div`
  display: flex;
  flex-direction: row;
  padding: 20px 30px;
  justify-content: center;
  border-bottom: 1px solid lightgray;
`;
const CoverImage = styled.img`
  height: 500px;
  width: 500px;
`;
const InfoColumn = styled.div`
  display: flex;
  flex-direction: column;
  margin: 50px;
`;
const MovieName = styled.span`
  font-size: 22px;
  font-weight: 600;
  color: white;
  margin: 25px 0;
  white-space: nowrap;
  overflow: hidden;
  text-transform: capitalize;
  text-overflow: ellipsis;
  & span {
    opacity: 0.8;
  }
`;
const MovieInfo = styled.span`
  font-size: 16px;
  font-weight: 500;
  color: white;
  overflow: hidden;
  margin: 10px 0;
  text-transform: capitalize;
  text-overflow: ellipsis;

  & span {
    opacity: 0.5;
  }
`;
const Close = styled.span`
  font-size: 16px;
  font-weight: 600;
  color: black;
  background: lightgray;
  height: fit-content;
  padding: 8px;
  border-radius: 50%;
  cursor: pointer;
  opacity: 0.8;
`;*/
const MovieInfoComponent = (props) => {
  const [movieInfo, setMovieInfo] = useState();
  const { selectedMovie } = props;

  useEffect(() => {
    Axios.get(
      `https://www.omdbapi.com/?i=${selectedMovie}&apikey=${API_KEY}`,
    ).then((response) => setMovieInfo(response.data));
  }, [selectedMovie]);
  return (
    </*Container*/div className="flex flex-row px-5 py-7 justify-center border-b-1-gray-600">
      {movieInfo ? (
        <>
          </*CoverImage*/img className="h-2/5 w-2/5" src={movieInfo?.Poster} alt={movieInfo?.Title} />
          </*InfoColumn*/div className="flex flex-col m-12.5">
            </*MovieName*/span className="text-3xl font-semibold text-white mx-7 my-2 whitespace-nowrap overflow-hidden capitalize text-ellipsis opacity-80">
              {movieInfo?.Type}: <span>{movieInfo?.Title}</span>
            </*MovieName*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              IMDB Rating: <span>{movieInfo?.imdbRating}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Year: <span>{movieInfo?.Year}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Language: <span>{movieInfo?.Language}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Rated: <span>{movieInfo?.Rated}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Released: <span>{movieInfo?.Released}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Runtime: <span>{movieInfo?.Runtime}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Genre: <span>{movieInfo?.Genre}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Director: <span>{movieInfo?.Director}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Actors: <span>{movieInfo?.Actors}</span>
            </*MovieInfo*//span>
            </*MovieInfo*/span className="text-base font-medium text-white overflow-hidden mx-2.5 my-0 capitalize text-ellipsis opacity-50">
              Plot: <span>{movieInfo?.Plot}</span>
            </*MovieInfo*//span>
          </*InfoColumn*//div>
          </*Close*/span className="text-base font-semibold text-black bg-gray-700" onClick={() => props.onMovieSelect()}>X</*Close*//span>
        </>
      ) : (
        "Loading..."
      )}
    </*Container*//div>
  );
};
export default MovieInfoComponent;
