import React, {useEffect, useState} from "react"
import { useParams } from "react-router-dom"

const Movie = () => {
    const [currentMovieDetail, setMovie] = useState()
    const { imdbID } = useParams()

    useEffect(() => {
        getData();
        window.scrollTo(0,0);
    }, []);

    const getData = () => {
        fetch(`https://www.omdbapi.com/?s=${imdbID}&apikey=e2b25841`)
        .then(res => res.json())
        .then(data => setMovie(data))
    }
//https://www.omdbapi.com/movie/?s=${id}&apikey=e2b25841
    return (
        <div className="movie">
            <div className="movie__detail">
                <div className="movie__detailLeft">
                    <div className="movie__posterBox">
                        <img className="movie__poster" src={currentMovieDetail ? currentMovieDetail.Poster : ""} />
                    </div>
                </div>
                <div className="movie__detailRight">
                    <div className="movie__detailRightTop">
                        <div className="movie__name">{currentMovieDetail ? currentMovieDetail.Title : ""}</div>
                        <div className="movie__releaseDate">{currentMovieDetail ? "Release date: " + currentMovieDetail.Year : ""}</div>
                    </div>
                    <div className="movie__detailRightBottom">
                        <div className="synopsisText">Synopsis</div>
                        <p></p>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Movie;
